import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

function SolutionDetail() {
  const { fileName } = useParams();  // Get the fileName from the URL
  const [content, setContent] = useState('');
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`/api/solution/${fileName}`)
      .then(async response => {
        if (!response.ok) {
          console.error('Server response:', response);
          // Check if the response is JSON
          const contentType = response.headers.get('content-type');
          if (contentType && contentType.indexOf('application/json') !== -1) {
            const jsonResponse = await response.json();
            throw new Error(jsonResponse.error || 'Network response was not ok');
          } else {
            throw new Error(`Received an unexpected response: ${response.statusText}`);
          }
        }
        return response.json();  // Parse the JSON response
      })
      .then(data => setContent(data.content))
      .catch(error => {
        console.error('Error fetching file content:', error);
        setError('Could not load file content. Please try again later.');
      });
  }, [fileName]);

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>Solution: {fileName}</h1>
      <pre>{content}</pre>  {/* Display the file content */}
    </div>
  );
}

export default SolutionDetail;
