import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

function SolutionDetail() {
  const { fileName } = useParams();  // Get the fileName from the URL
  const [content, setContent] = useState('');
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`/api/solution/${fileName}`)  // Fetch the content of the file
      .then(response => {
        if (!response.ok) {
          // If the response is not OK, handle the error
          throw new Error('Network response was not ok');
        }
        return response.json();  // Parse the JSON response
      })
      .then(data => setContent(data.content))  // Set the file content in the state
      .catch(error => {
        console.error('Error fetching file content:', error);
        setError('Could not load file content. Please try again later.');
      });
  }, [fileName]);  // Depend on fileName, so the effect runs again if it changes

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>Solution: {fileName}</h1>
      <pre>{content}</pre>  {/* Display the file content */}
    </div>
  );
}

export default SolutionDetail;
